:root {
	--theme-color: #e19bb7;
	--header-height: 30rem;
	--banner-bg-color: transparent; 
  --font-fans: SFCompactRounded,PingFang SC,Microsoft YaHei,Lato,sans-serif;
}

/* header overall */


.xlog-site-name {
    color: var(--theme-color);
    font-family: Candyshop;
    text-shadow: -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, 1px 1px 0 #fff, -1.414px 0 0 #fff, 1.414px 0 0 #fff, 0 -1.414px 0 #fff, 0 1.414px 0 #fff;
    font-size: 35px;
		font-weight: 100;
    letter-spacing: 0.2rem;
		text-transform: capitalize;
}

.xlog-site-info {
    background-color: rgb(255 255 255 / 40%);
    backdrop-filter: blur(4px);
    -webkit-backdrop-filter: blur(4px);
    border-radius: 0.75rem;
    padding: 2rem;
    z-index: 1;
    border: 1px solid #fff;
    transition: .3s ease;
}

/** Navigator */
.xlog-header>div:last-child>div:last-child {
		padding: 0 1rem;
    background-color: rgb(255 255 255 / 45%); 
		backdrop-filter: blur(3px);
		-webkit-backdrop-filter: blur(1.5px);
		z-index: 10;
}

.xlog-header>div:last-child { 
	padding-left: 0;
	padding-right: 0;
} 

/** Button Group */
@media (min-width: 640px) {
	.xlog-header>div:last-child>div:nth-child(2) {
		padding: 3rem 4rem;
	}

	.xlog-site-description,.xlog-site-follow-count>button, .xlog-site-patron>button>span>span {
		color: whitesmoke;
	}

	.xlog-site-follow-count>button, .xlog-site-follow-button, .xlog-site-more-out svg {
		transition: all .3s ease;
	}
	.xlog-site-follow-count>button:hover, .xlog-site-patron>button:hover,.xlog-site-follow-button:hover, .xlog-site-more-out:hover{ 
		animation: shake 2s ease;
	}
	.xlog-site-follow-count>button.is-text, .xlog-site-patron>button.is-text {
		background-color: #e19bb7;
		transition-property: all;
		transition: .3s ease;
	}
	.xlog-site-follow-count>button.is-text:hover, .xlog-site-follow-button>button.is-primary.rounded-lg:hover, .xlog-site-patron>button.is-text:hover {
		background-color: #f3bac9; 
	}
}

/** Site Icon Shake */
.xlog-site-icon {
    width: 110px;
    height: 110px; 
}

.xlog-site-icon:hover {  
	animation: shake 2s ease;
}

@media (max-width: 640px) {
    .xlog-site-info {
        padding: 1rem;
    }
		.xlog-site-info {
			margin: 0 1rem;
		}
}

.xlog-site-navigation-item:hover {
	color: #e19bb7;
}

.xlog-site-navigation-item:hover::after {
	background-color: #e19bb7;
}

/* post list */

.xlog-post {
    transition: .3s ease;
}

@media (min-width: 640px) {
    .xlog-post:hover {
        transform: scale(1.02) translateY(-0.25rem);
				transition: all 0.3s ease;
        box-shadow: rgb(0 0 0 / 10%) 0 10px 50px;
        background: #fff;
    }

    .xlog-site-info:hover {
        transform: scale(1.03);
        -webkit-font-smoothing: antialiased;
    }
		
}

