:root {
  --theme-color: #ff9800;
  --font-fans: SFCompactRounded,PingFang SC,Microsoft YaHei,Lato,sans-serif;
  --header-height: max(50vh, 400px);
  --banner-bg-color: #fff;
  cursor: url(ipfs://bafkreihm4v5gpjkv5lboaqhdfmou4mlpvfjkgbn24ywrfivdk7tolgzkke), default;
}

/* header overall */

.xlog-banner img {
    width: auto !important;
}

.xlog-site-name {
    color: var(--theme-color);
    font-family: Candyshop;
    text-shadow: -1px -1px 0 #fff, 1px -1px 0 #fff, -1px 1px 0 #fff, 1px 1px 0 #fff, -1.414px 0 0 #fff, 1.414px 0 0 #fff, 0 -1.414px 0 #fff, 0 1.414px 0 #fff;
    font-size: 35px;
}

.xlog-site-info {
    background-color: rgb(255 255 255 / 50%);
    backdrop-filter: blur(4px);
    -webkit-backdrop-filter: blur(4px);
    border-radius: 0.75rem;
    padding: 2rem;
    z-index: 1;
    border: 1px solid #fff;
    transition: .3s ease;
}

.xlog-site-icon {
    width: 110px;
    height: 110px;
}

@media (max-width: 640px) {
    .xlog-site-info {
        padding: 1rem;
    }
}

/* post list */

.xlog-post {
    transition: .3s ease;
}

@media (min-width: 640px) {
    .xlog-post:hover {
        transform: scale(1.02);
        box-shadow: rgb(0 0 0 / 10%) 0 10px 50px;
        background: #fff;
    }

    .xlog-site-info:hover {
        transform: scale(1.03);
        -webkit-font-smoothing: antialiased;
    }
}

/* footer */

footer {
    position: relative;
    margin-top: 210px;
}

footer:before {
    content: ' ';
    background: url(ipfs://bafkreigq3m2bj76jl7abz6i5rlzegutcrfc2j2nqkcypksbeyu67fsymoy) no-repeat center;
    height: 368px;
    width: 100%;
    display: block;
    position: absolute;
    top: -295px;
    left: 0;
    pointer-events: none;
}

